
public class Latihan07 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String kata="Mahesa Alghifari";
		
		kata=kata.replaceAll("a", "x");
		kata=kata.replaceAll("i", "x");
		kata=kata.replaceAll("u", "x");
		kata=kata.replaceAll("e", "x");
		kata=kata.replaceAll("o", "x");
		
		System.out.println(kata);
	}

}
